class City:
    def __init__(self, centerX:int, centerY:int):
        self.__centerX = centerX
        self.__centerY = centerY

    def GetCenterX(self):
        return self.__centerX
    def SetCenterX(self, centerX:int):
        self.__centerX = centerX

    def GetCenterY(self):
        return self.__centerY
    def SetCenterX(self, centerY:int):
        self.__centerY = centerY