"""
Functions for generating UTF-formatted text (e.g. bold, italic, small-caps,
etc.) 
"""

class UtfSub:
    def __init__( self, *replacement ):
        if len( replacement ) == 1:
            replacement = replacement[0]
        
        if isinstance( replacement, tuple ):
            replacement = "-----------------" + replacement[0] + "-------" + replacement[1] + "------" + replacement[2] + "-----"
        
        if len( replacement ) != len( _normal_str ):
            raise ValueError( "Replacement text {} but must be {} characters long:\n{}\n{}".format( len( replacement ), len( normal ), replacement, normal ) )
        
        self.replacement = replacement
    
    
    def __call__( self, text ):
        replacement = self.replacement
        r = []
        
        for c in str( text ):
            index = ord( c ) - 31
            
            if index > len( replacement ):
                # raise ValueError( "Index ({}, {}) above replacement length ({}).".format( c, index, len( replacement ) ) )
                r.append( c )
            elif replacement[index] == "-":
                r.append( c )
            else:
                r.append( replacement[index] )
        
        return "".join( r )


