import openseespy.opensees as opy
opy.wipe()
opy.model('basic', '-ndm', 2, '-ndf', 3)
opy.node(1, 0.0, 0.0, '-mass', 0.0, 0.0, 0.0)
opy.node(2, 4.0, 0.0, '-mass', 0.0, 0.0, 0.0)
opy.fix(1, 1, 1, 1)
opy.fix(2, 0, 0, 0)
opy.section('Elastic', 1, 200.0, 0.5, 0.1)
opy.section('Elastic', 2, 200.0, 0.5, 0.1)
opy.section('Elastic', 3, 200.0, 0.5, 0.1)
opy.beamIntegration('HingeMidpoint', 1, 1, 0.1, 2, 0.1, 3)
opy.geomTransf('Linear', 1)
opy.element('forceBeamColumn', 1, 1, 2, 1, 1)
opy.timeSeries('Linear', 1, '-factor', 1.0)
opy.pattern('Plain', 1, 1)
opy.eleLoad('-ele', 1, '-type', '-beamUniform', -10.0)
opy.constraints('Plain')
opy.numberer('RCM')
opy.system('BandGeneral')
opy.integrator('LoadControl', 0.1, 10)
opy.test('NormDispIncr', 0.001, 10, 0, 2)
opy.algorithm('Linear', False, False, False)
opy.analysis('Static')
opy.analyze(10, 0.1)
opy.reactions()
opy.eleResponse(1, 'force')
print(opy.eleResponse(1, 'force'))
print(opy.nodeDisp(2, 2))
