# simple types
BooleanFormat = '^((True)|(False)|(true)|(false)|(0)|(1))$'
IntFormat = '^[0-9]{1,32}$'
FloatFormat = '^[-+]?[0-9]*\.?[0-9]+$'
IntervalFormat = '^\d{0,10}\,\d{0,10}$'

# text formats
EmptyFormat = '^$'
BaseFormat = '^[\w\W]*$'

UUIDFormat = '^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$'
EmailFormat = '^(?:[a-z0-9_-]+(?:\.[a-z0-9!#$%&\'*+/=?^_' \
              '`{|}~-]+)*|"(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21\x23-\x5b\x5d-' \
              '\x7f]|\\[\x01-\x09\x0b\x0c\x0e-\x7f])*")@(?:(?:[a-z0-9]' \
              '(?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?' \
              '|\[(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}' \
              '(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?|[a-z0-9-]*[a-z0-9]:' \
              '(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21-\x5a\x53-\x7f]|\\[\x01-' \
              '\x09\x0b\x0c\x0e-\x7f])+)\])$'
