taixuandict = {1111:{'卦': {'中': '陽氣潛萌於黃宮，信無不在乎中。'},
  '初一': '昆侖旁薄，幽。測曰，「昆侖旁薄」、思諸貞也。',
  '次二': '神戰于玄，其陳陰陽。測曰，「神戰于玄」、善惡并也。',
  '次三': '龍出于中，首尾信，可以為庸。測曰，「龍出于中」、見其造也。',
  '次四': '庳虛無因，大受性命，否。測曰，「庳虛」無「否」、不能大受也。',
  '次五': '日正于天，利以其辰作主。測曰，「日正于天」、貴當位也。',
  '次六': '月闕其博，不如開明于西。測曰，「月闕其博」、明始退也。',
  '次七': '酋酋，大魁頤，水包貞。測曰，「酋酋」之「包」、任臣則也。',
  '次八': '黃不黃，覆秋常。測曰，「黃不黃」、失中德也。',
  '上九': '巔靈，氣形反。測曰，「巔靈」之「反」、時不克也。'},

 1112:{'卦': {'周': '陽氣周神而反乎始，物繼其彙。'},
  '初一': '還於天心，何德之僭，否。測曰，「還心」之「否」、中不恕也。',
  '次二': '植中樞，周無隅。測曰，「植中樞」、立督慮也。',
  '次三': '出我入我，吉凶之魁。測曰，「出我入我」、不可不懼也。',
  '次四': '帶其鉤鞶，錘以玉鐶。測曰，「帶其鉤鞶」、自約束也。',
  '次五': '土中其廬，設于金輿，厥戒渝。測曰，「廬」「金」「戒渝」、小人不克也。',
  '次六': '信周其誠，上通于天。測曰，「信周其誠」、上通也。',
  '次七': '豐淫，見其朋，還于蒙，不克從。測曰，「豐淫」「見朋」、不能從也。',
  '次八': '還遇躬外，其禍不大。測曰，「還遇躬外」、禍不中也。',
  '上九': '還于喪，或棄之行。測曰，「還于喪」、其道窮也。'},
 
 1113:{'卦': {'礥': '陽氣微動，動而礥礥，物生之難也。'},
  '初一': '黃純于潛，不見其畛，藏鬱於泉。測曰，「黃純于潛」、化在嘖也。',
  '次二': '黃不純，屈于根。測曰，「黃不純」、失中適也。',
  '次三': '赤子扶扶，元貞有終。測曰，「赤子扶扶」、父母瞻也。',
  '次四': '拔我不德，以力不克。測曰，「拔我德」、力不堪也。',
  '次五': '拔車山淵，宜於大人。測曰，「拔車山淵」、大位力也。',
  '次六': '將其車，入于丘虛。測曰，「將車」「入虛」、道不得也。',
  '次七': '出險登丘，或牽之牛。測曰，「出險登丘」、莫之伐也。',
  '次八': '車不拔，髀軸折。測曰，「車不拔」、躬自賊也。',
  '上九': '崇崇高山，下有川波其，人有輯航，可與過其。測曰，「高山」大「川」、不輯航不克也。'},
 
 1121:{'卦': {'閑': '陽氣閑於陰，礥然物咸見閑。'},
  '初一': '蛇伏於泥，無雄有雌，終莫受施。測曰，「蛇伏于泥」、君不君也。',
  '次二': '閑其藏，固珍寶。測曰，「閑其藏」、中心淵也。',
  '次三': '關無鍵，舍金管。測曰，「關無鍵」、盜入門也。',
  '次四': '拔我輗軏，小得利小征。測曰，「拔我輗軏」、貴以信也。',
  '次五': '礥而閑而，拔我姦而，非石如石，厲。測曰，「礥」「閑」「如石」、其敵堅也。',
  '次六': '閑黃垁，席金笫。測曰，「閑黃垁」、以德固也。',
  '次七': '跙跙，閑于蘧除，或寢之廬。測曰，「跙跙」之「閑」、惡在舍也。',
  '次八': '赤臭播關，大君不閑，克國乘家。測曰，「赤臭播關」、恐入室也。',
  '上九': '閑門以終，虛。測曰，「閑門」以「虛」、終不可實也。'},
 
 1122:{'卦': {'少': '陽氣澹然施於淵，物溓然能自韱。'},
  '初一': '冥自少，眇于謙。測曰，「冥自少」、不見謙也。',
  '次二': '自少不至，懷其䘏。測曰，「自少不至」、謙不成也。',
  '次三': '動韱其，得人主之式。測曰，「韱其」「得人」、謙貞也。',
  '次四': '貧貧，或妄之振。測曰，「貧貧」「妄振」、不能守正也。',
  '次五': '地自沖，下于川。測曰，「地自沖」、人之所聖也。',
  '次六': '少持滿，今盛後傾。測曰，「少持滿」、何足盛也。',
  '次七': '貧自究，利用見富。測曰，「貧自究」、富之聘也。',
  '次八': '貧不貧，人莫之振。測曰，「貧不貧」、何足敬也。',
  '上九': '密雨溟沐，潤于枯瀆，三日射谷。測曰，「密雨」「射谷」、謙之靜也。'},
 
 1123:{'卦': {'戾': '陽氣孚微，物各乖離而觸其類。'},
  '初一': '虛既邪，心有傾。測曰，「虛邪」「心傾」、懷不正也。',
  '次二': '正其腹，引其背，酋貞。測曰，「正其腹」、中心定也。',
  '次三': '戾其腹，正其背。測曰，「戾腹」「正背」、中外爭也。',
  '次四': '夫妻反道，維家之保。測曰，「夫妻反道」、各有守也。',
  '次五': '東南射兕，西北其矢。測曰，「東南射兕」、不得其首也。',
  '次六': '準繩規矩，不同其施。測曰，「準繩規矩」、乖其道也。',
  '次七': '女不女，其心予，覆夫諿。測曰，「女不女」、大可醜也。',
  '次八': '殺生相矢，中和其道。測曰，「殺生相矢」、中為界也。',
  '上九': '倉靈之雌，不同宿而離失，則歲之功乖。測曰，「倉靈之雌」、失作敗也。'},
 
 1131:{'卦': {'上': '陽氣育物于下，咸射地而登乎上。'},
  '初一': '上其純心，挫厥鏩鏩。測曰，「上其純心」、和以悅也。',
  '次二': '上無根，思登于天，谷在于淵。測曰，「上無根」、不能自治也。',
  '次三': '出于幽谷，登于茂木，思其珍穀。測曰，「出谷」「登木」、知向方也。',
  '次四': '即上不貞，無根繁榮，孚虛名。測曰，「即上不貞」、妄升也。',
  '次五': '鳴鶴升自深澤，階天不怍。測曰，「鳴鶴」「不怍」、有諸中也。',
  '次六': '升于堂，顛衣到裳，廷人不慶。測曰，「升堂」「顛到」、失大眾也。',
  '次七': '升于顛臺，或柱之材。測曰，「升臺」「柱」、輔弗堅也。',
  '次八': '升于高危，或斧之梯。測曰，「升危」「斧梯」、失士民也。',
  '上九': '棲于菑，初亡後得基。測曰，「棲菑」「得基」、後得人也。'},
 
 1132:{'卦': {'干': '陽氣扶物而鑽乎堅，鉿然有穿。'},
  '初一': '丸鑽鑽于內隙，厲。測曰，「丸鑽于內」、轉丸非也。',
  '次二': '以微干正，維用軌命。測曰，「以微干正」、大諫微也。',
  '次三': '𦴑鍵挈挈，匪貞。測曰，「𦴑鍵挈挈」、干祿回也。',
  '次四': '干言入骨，時貞。測曰，「干骨」之「時」、直其道也。',
  '次五': '蚩蚩干于丘飴，或錫之坏。測曰，「蚩蚩」之「干」、錫不好也。',
  '次六': '幹干於天，貞馴。測曰，「幹干」之「貞」、順可保也。',
  '次七': '何戟解解，遘。測曰，「何戟解解」、不容道也。',
  '次八': '赤舌燒城，吐水于缾。測曰，「赤舌」「吐水」、君子以解祟也。',
  '上九': '干于浮雲，從墜于天。測曰，「干于浮雲」、乃從天墜也。'},
 
 1133:{'卦': {'𤕠': '陽氣彊內而弱外，物咸扶𤕠而進乎大。'},
  '初一': '自我匍匐，好是宜德。測曰，「匍匐」「宜德」、若無行也。',
  '次二': '熒𤕠狧狧，不利有攸往。測曰，「熒𤕠狧狧」、多欲往也。',
  '次三': '卉炎于𤕠，宜于丘陵。測曰，「卉炎」「丘陵」、短臨長也。',
  '次四': '𤕠于酒食，肥無譽。測曰，「𤕠于酒食」、仕無方也。',
  '次五': '𤕠有足，託堅穀。測曰，「𤕠有足」、位正當也。',
  '次六': '獨𤕠逝逝，利小不利大。測曰，「獨𤕠逝逝」、不可大也。',
  '次七': '白日臨辰，可以卒其所聞。測曰，「白日臨辰」、老得勢也。',
  '次八': '蚤虱之𤕠，厲。測曰，「蚤虱之𤕠」、不足賴也。',
  '上九': '全𤕠，絭其首尾，臨于淵。測曰，「全𤕠」之「絭」、恐遇困也。'},
 
 1211:{'卦': {'羡': '陽氣贊幽推包，羡爽未得正行。'},
  '初一': '羡於初，其次迂塗。測曰，「羡于初」、後難正也。',
  '次二': '羡于微，克復，可以為儀。測曰，「羡微」「克復」、不遠定也。',
  '次三': '羡迂塗，不能直如。測曰，「羡迂塗」、不能直行也。',
  '次四': '羡權正，吉人不幸。測曰，「羡權正」、善反常也。',
  '次五': '孔道夷如，蹊路微如，大輿之憂。測曰，「孔道」之「夷」、奚不遵也。',
  '次六': '大虛既邪，或直之，或翼之，得矢夫。測曰，「虛邪」「矢夫」、得賢臣也。',
  '次七': '曲其故，迂其塗，厲之馴。測曰，「曲其故」、為作意也。',
  '次八': '羡其足，濟于溝瀆，面貞。測曰，「羡其足」、避凶事也。',
  '上九': '車軸折，其衡抈，四馬就括，高人吐血。測曰，「軸折」「吐血」、終不可悔也。'},
 
 1212:{'卦': {'差': '陽氣蠢闢於東，帝由群雍，物差其容。'},
  '初一': '微失自攻，端。測曰，「微失自攻」、人未知也。',
  '次二': '𥧲其所好，將以致其所惡。測曰，「𥧲其所好」、漸以差也。',
  '次三': '其亡其亡，將至于暉光。測曰，「其亡其亡」、震自衛也。',
  '次四': '過小善，不克。測曰，「過小善」、不能至大也。',
  '次五': '過門折入，得此中行。測曰，「過門折入」、近復還也。',
  '次六': '大跌，過其門，不入其室。測曰，「大跌」「不入」、誠可患也。',
  '次七': '累卵業業，懼貞安。測曰，「累卵業業」、自危作安也。',
  '次八': '足纍纍，其步蹉躟，輔銘滅麋。測曰，「足纍纍」、履禍不還也。',
  '上九': '過其枯城，或蘖青青。測曰，「過其枯城」、改過更生也。'},
 
 1213:{'卦': {'童': '陽氣始窺，物僮然咸未有知。'},
  '初一': '顓童不寤，會我蒙昏。測曰，「童不寤」、恐終晦也。',
  '次二': '錯于靈蓍，焯于龜資，出泥入脂。測曰，「錯蓍」「焯龜」、比光道也。',
  '次三': '東辰以明，不能以行。測曰，「東辰以明」、奚不逝也？',
  '次四': '或後前夫，先錫之光。測曰，「或後前夫」、先光大也。',
  '次五': '蒙柴求兕，其德不美。測曰，「蒙柴求兕」、得不慶也。',
  '次六': '大開帷幕，以引方客。測曰，「大開帷幕」、覽眾明也。',
  '次七': '脩侏侏，比于朱儒。測曰，「侏侏」之「脩」、無可為也。',
  '次八': '或擊之，或剌之，修其玄鑒，渝。測曰，「擊之」「剌之」、過以衰也。',
  '上九': '童麋觸犀，灰其首。測曰，「童麋觸犀」、還自纍也。'},
 
 1221:{'卦': {'增': '陽氣蕃息，物則益增，日宣而殖。'},
  '初一': '聞貞增默，外人不得。測曰，「聞貞增默」、識內也。',
  '次二': '不增其方，而增其光，冥。測曰，「不增其方」、徒飾外也。',
  '次三': '木以止，漸增。測曰，「木止」「漸增」、不可益也。',
  '次四': '要不克，或增之戴。測曰，「要不克」、可敗也。',
  '次五': '澤庳其容，眾潤攸同。測曰，「澤庳其容」、謙虛大也。',
  '次六': '朱車燭分，一日增我三千，君子慶，小人傷。測曰，「朱車」之「增」、小人不當也。',
  '次七': '增其高，刃其削，丘貞。測曰，「增高」「刃峭」、與損皆行也。',
  '次八': '兼貝以役，往益來𨲞。測曰，「兼貝以役」、前慶後亡也。',
  '上九': '崔嵬不崩，賴彼峽崥。測曰，「崔嵬不崩」、群士橿橿也。'},
 
 1222:{'卦': {'銳': '陽氣岑以銳，物之生也咸專一而不二。'},
  '初一': '蟹之郭索，後蚓黃泉。測曰，「蟹之郭索」、心不一也。',
  '次二': '銳一，無不達。測曰，「銳一」之「達」、執道必也。',
  '次三': '狂銳，盪。測曰，「狂銳」之「盪」、不能處一也。',
  '次四': '銳于時，無不利。測曰，「銳于時」、得其適也。',
  '次五': '銳其東，忘其西，見其背，不見其心。測曰，「銳東」「忘西」、不能迴避也。',
  '次六': '銳于醜，含于五軌萬鍾，貞。測曰，「銳于醜」、福祿不量也。',
  '次七': '銳于利，忝惡至。測曰，「銳于利」、辱在一方也。',
  '次八': '銳其銳，救其敗。測曰，「銳其銳」、恐轉作殃也。',
  '上九': '陵崢岸峭，陁。測曰，「陵崢岸峭」、銳極必崩也。'},
 
 1223:{'卦': {'達': '陽氣枝枝條出，物莫不達。'},
  '初一': '中冥獨達，迵迵不屈。測曰，「中冥獨達」、內曉無方也。',
  '次二': '迷腹達目。測曰，「迷腹達目」、以道不明也。',
  '次三': '蒼木維流，厥美可以達于瓜苞。測曰，「蒼木維流」、內恕以量也。',
  '次四': '小利小達，大迷扁扁，不故。測曰，「小達」「大迷」、獨曉隅方也。',
  '次五': '達于中衢，大小無迷。測曰，「達于中衢」、道四通也。',
  '次六': '大達無畛，不要止洫作，否。測曰，「大達無畛」、不可偏從也。',
  '次七': '達于砭割，前亡後賴。測曰，「達于砭割」、終以不廢也。',
  '次八': '迷目達腹。測曰，「迷目達腹」、外惑其內也。',
  '上九': '達于咎，貞，終譽。測曰，「達咎」「終譽」、善以道退也。'},
 
 1231:{'卦': {'交': '陽交於陰，陰交於陽，物登明堂，矞矞皇皇。'},
  '初一': '冥交于神，齊不以其貞。測曰，「冥交」「不以」、懷非含慚也。',
  '次二': '冥交有孚，明如。測曰，「冥交」之「孚」、信接神明也。',
  '次三': '交于木石。測曰，「交于木石」、不能嚮人也。',
  '次四': '往來熏熏，得亡之門。測曰，「往來熏熏」、與神交行也。',
  '次五': '交于鸎猩，不獲其榮。測曰，「交于鸎猩」、鳥獸同方也。',
  '次六': '大圈閎閎，小圈交之，我有靈殽，與爾殽之。測曰，「大」「小」之「交」、待賢煥光也。',
  '次七': '交于鳥鼠，費其資黍。測曰，「交于鳥鼠」、徒費也。',
  '次八': '戈矛往來，以其貞，不悔。測曰，「戈矛往來」、征不可廢也。',
  '上九': '交于戰伐，不貞，覆于城，猛則噉。測曰，「交于戰伐」、奚可遂也？'},
 
 1232:{'卦': {'䎡': '陽氣能剛能柔，能作能休，見難而縮。'},
  '初一': '赤卉方銳，利進以退。測曰，「赤卉方銳」、退以動也。',
  '次二': '䎡其心，作疾。測曰，「䎡其心」、中無勇也。',
  '次三': '䎡其䣛，守其節，雖勿肆，終無拂。測曰，「䎡其䣛」、體不可肆也。',
  '次四': '䎡其哇，三歲不噣。測曰，「䎡哇」「不噣」、時數失也。',
  '次五': '黃菌不誕，俟于慶雲。測曰，「黃菌不誕」、俟逑耦也。',
  '次六': '縮失時，或承之菑。測曰，「縮失時」、坐逋後也。',
  '次七': '詘其節，執其術，共所歾。測曰，「詘節」「共歾」、內有主也。',
  '次八': '窾枯木丁衝，振其枝，小人有䎡，三郄鉤羅。測曰，「窾木」之「振」、小人見侮也。',
  '上九': '悔縮，往去來復。測曰，「悔縮」之「復」、得在後也。'},
 
 1233:{'卦': {'傒': '陽氣有傒，可以進而進，物咸得其願。'},
  '初一': '冥賊，傒天凶。測曰，「冥賊」之「傒」、時無吉也。',
  '次二': '冥德，傒天昌。測曰，「冥德」之「傒」、昌將日也。',
  '次三': '傒後時。測曰，「傒」而「後」之、解也。',
  '次四': '詘其角，直其足，維以傒穀。測曰，「屈角」「直足」、不伎剌也。',
  '次五': '大爵集于宮庸，小人庳，傒空。測曰，「宮庸」之「爵」、不可空得也。',
  '次六': '傒福貞貞，食于金。測曰，「傒福貞貞」、正可服也。',
  '次七': '傒禍介介，凶人之郵。測曰，「傒禍介介」、與禍期也。',
  '次八': '不禍禍，傒天活我。測曰，「禍」「不禍」、非厥訧也。',
  '上九': '傒尪尪，天撲之顙。測曰，「傒尪」之「撲」、終不可治也。'},
 
 1311:{'卦': {'從': '陽躍于淵、于澤，于田、于嶽，物企其足。'},
  '初一': '日幽嬪之，月冥隨之，基。測曰，「日嬪」「月隨」、臣應基也。',
  '次二': '方出旭旭，朋從爾醜。測曰，「方出」「朋從」、不知所之也。',
  '次三': '人不攻之，自牽從之。測曰，「人不攻之」、自然證也。',
  '次四': '鳴從不臧，有女承其血，匡亡。測曰，「鳴從」之「亡」、奚足朋也。',
  '次五': '從水之科，滿。測曰，「從水」「滿科」、不自越也。',
  '次六': '從其目，失其腹。測曰，「從目」「失腹」、欲丕從也。',
  '次七': '拂其惡，從其淑，雄黃食肉。測曰，「拂惡」「從淑」、救凶也。',
  '次八': '從不淑，禍飛不逐。測曰，「從不淑」、禍不可訟也。',
  '上九': '從徽徽，後乃登于階，終。測曰，「從徽徽」、後得功也。'},
 
 1312:{'卦': {'進': '陽引而進，物出溱溱，開明而前。'},
  '初一': '冥進否，作退母。測曰，「冥進否」、邪作退也。',
  '次二': '進以中刑，大人獨見。測曰，「進以中刑」、刑不可外也。',
  '次三': '狂章章，不得中行。測曰，「狂章章」、進不中也。',
  '次四': '日飛懸陰，萬物融融。測曰，「飛懸陰」、君道隆也。',
  '次五': '進以欋䟽，或杖之扶。測曰，「進以欋䟽」、制于尊也。',
  '次六': '進以高明，受祉無疆。測曰，「進以高明」、其道迂也。',
  '次七': '進非其以，聽咎窒耳。測曰，「進非其以」、毀滋章也。',
  '次八': '進于淵，君子用䟽。測曰，「進淵」且「䟽」、以道行也。',
  '上九': '逆憑山川，三歲不還。測曰，「逆憑山川」、終不可長也。'},
 
 1313:{'卦': {'釋': '陽氣和震圜煦，釋物咸稅其枯，而解其甲。'},
  '初一': '動而無名，酋。測曰，「動而無名」、不可得名也。',
  '次二': '動于響景。測曰，「動于響景」、不足觀聽也。',
  '次三': '風動雷興，從其高崇。測曰，「風動雷興」、動有為也。',
  '次四': '動之丘陵，失澤朋。測曰，「動之丘陵」、失下危也。',
  '次五': '和釋之脂，四國之夷。測曰，「和釋之脂」、民說無疆也。',
  '次六': '震于廷，喪其和貞。測曰，「震于廷」、和正俱亡也。',
  '次七': '震震不侮，濯漱其詬。測曰，「震震不侮」、解恥無方也。',
  '次八': '震于利，巔仆死。測曰，「震于利」、與死偕行也。',
  '上九': '今獄後穀，終說桎梏。測曰，「今獄後穀」、于彼釋殃也。'},

1321:{'卦': {'格': '陽氣內壯，能格乎群陰，攘而郄之。'},
  '初一': '格內善，失貞類。測曰，「格內善」、不省也。',
  '次二': '格內惡，幽貞類。測曰，「格內惡」、幽貞妙也。',
  '次三': '裳格鞶鉤，渝。測曰，「裳格鞶鉤」、無以制也。',
  '次四': '畢格，禽鳥之貞。測曰，「畢格」「禽」、正法位也。',
  '次五': '膠漆釋，弓不射，角木離。測曰，「膠漆釋」、信不結也。',
  '次六': '息金消石，往小來弈。測曰，「息金消石」、美日大也。',
  '次七': '格其珍類，龜緺厲。測曰，「格其珍類」、無以自匡也。',
  '次八': '格彼鞶堅，君子得時，小人𨲞，憂否。測曰，「格彼鞶堅」、誼不得行也。',
  '上九': '郭其目，䚩其角，不庳其體，撲。測曰，「郭目解角」、還自傷也。'},
 
 1322:{'卦': {'夷': '陽氣傷𨲞，陰無救瘣，物則平易。'},
  '初一': '載幽貳，執夷內。測曰，「載幽」「執夷」、易其內也。',
  '次二': '陰夷，冒于天罔。測曰，「陰夷」「冒罔」、䟽不失也。',
  '次三': '柔，嬰兒于號，三日不嗄。測曰，「嬰兒于號」、中心和也。',
  '次四': '夷其牙，或飫之徒。測曰，「夷其牙」、食不足嘉也。',
  '次五': '中夷，無不利。測曰，「中夷」之「利」、其道多也。',
  '次六': '夷于廬，其宅丘虛。測曰，「夷于廬」、厥德亡也。',
  '次七': '榦柔榦弱，離木艾金，夷。測曰，「榦柔」「艾金」、弱勝彊也。',
  '次八': '夷其角，厲。測曰，「夷其角」、以威傷也。',
  '上九': '夷于耇利，敬病年貞。測曰，「夷耇」之「貞」、懸車鄉也。'},
 
 1323:{'卦': {'樂': '陽始出奧舒，疊得以和淖，物咸喜樂。'},
  '初一': '獨樂款款，及不遠。測曰，「獨樂款款」、淫其內也。',
  '次二': '樂不知，辰于天。測曰，「樂不可知」、以時歲也。',
  '次三': '不宴不雅，𠹑呱啞咋，號咷倚戶。測曰，「不宴不雅」、禮樂廢也。',
  '次四': '拂其繫，絕其纗，佚厥心。測曰，「拂其繫」「絕纗」、心誠快也。',
  '次五': '鍾鼓喈喈，管絃嚌嚌，或承之衰。測曰，「鍾鼓喈喈」、樂後悲也。',
  '次六': '大樂無間，民、神、禽鳥之般。測曰，「大樂無間」、無不懷也。',
  '次七': '人嘻鬼嘻，天要之期。測曰，「人嘻鬼嘻」、稱樂畢也。',
  '次八': '嘻嘻自懼，亡彼愆虞。測曰，「嘻嘻自懼」、終自保也。',
  '上九': '極樂之幾，不移日而悲，則哭泣之𨲠資。測曰，「極樂之幾」、信可悔也。'},
 
 1331:{'卦': {'爭': '陽氣汜施，不偏不頗，物與爭訟，各遵其儀。'},
  '初一': '爭不爭，隱冥。測曰，「爭不爭」、道之素也。',
  '次二': '赫河，臞。測曰，「赫河」之「臞」、何可怙也。',
  '次三': '爭射齦齦。測曰，「爭射誾誾」、君子讓鄰也。',
  '次四': '爭小利，不酋貞。測曰，「小利」不絕、正道乃昏也。',
  '次五': '爭于逵，利以無方。測曰，「爭于逵」、爭處中也。',
  '次六': '臂膊脛如，股腳䐜如，維身之疾。測曰，「臂脛」「如股」、臣大隆也。',
  '次七': '爭干及矛䩜，用亨于王前行。測曰，「干」「矛」之爭、衛君躬也。',
  '次八': '狼盈口，矢在其後。測曰，「狼盈口」、不顧害也。',
  '上九': '兩虎相牙，知掣者全。測曰，「兩虎相牙」、知所制也。'},
 
 1332:{'卦': {'務': '陽氣勉務，物咸若其心而揔其事。'},
  '初一': '始務無方，小人亦用，罔。測曰，「始務無方」、非小人所理也。',
  '次二': '新鮮自求珍，絜精其芳，君子攸行。測曰，「新鮮自求」、光于己也。',
  '次三': '不拘不掣，其心腐且敗。測曰，「不拘不掣」、其體不全也。',
  '次四': '見矢自升，利羽之朋，蓋戴車載。測曰，「矢」及「蓋」、厥道然也。',
  '次五': '蜘蛛之務，不如蠶之緰。測曰，「蜘蛛之務」、無益人也。',
  '次六': '華實芳若，用則臧若。測曰，「華芳」「用臧」、利當年也。',
  '次七': '喪其芳，無攸往。測曰，「喪其芳」、德以衰也。',
  '次八': '黃中，免于禍，貞。測曰，「黃中」「免禍」、和以正也。',
  '上九': '務成自敗，雨成自隊。測曰，「務成自敗」、非厥命也。'},
 
 1333:{'卦': {'事': '陽氣大冒昭職，物則信信，各致其力。'},
  '初一': '事無事，至無不事。測曰，「事無事」、以道行也。',
  '次二': '事在樞，不咨不諏，喪其哲符。測曰，「不咨不諏」、其知亡也。',
  '次三': '時往時來，間不容氂。測曰，「時往時來」、不失趣也。',
  '次四': '男女事，不代之字。測曰，「男女事」、非厥務也。',
  '次五': '事其事，王假之食。測曰，「事其事」、職所任也。',
  '次六': '任大自事，方來不救。測曰，「任大自事」、奚可堪也。',
  '次七': '丈人扶孤，豎子提壺。測曰，「丈人扶孤」、小子知方也。',
  '次八': '男女事，十年不誨。測曰，「男女事」、終家不亨也。',
  '上九': '到耳順止，事貞。測曰，「到耳順止」、逆聞順行也。'},
 
 2111:{'卦': {'更': '陽氣既飛，變勢易形，物改其靈。'},
  '初一': '冥化否貞，若性。測曰，「冥化否貞」、少更方也。',
  '次二': '時七時九，軫轉其道。測曰，「時七時九」、不失當也。',
  '次三': '化白于泥，淄。測曰，「化白于泥」、變不明也。',
  '次四': '更之小得，用無不利。測曰，「更之小得」、民所望也。',
  '次五': '童牛角馬，不今不古。測曰，「童牛角馬」、變天常也。',
  '次六': '入水載車，出水載杭，宜于王之更。測曰，「車杭」「出入」、其道更也。',
  '次七': '更不更，以作病。測曰，「更不更」、能自臧也。',
  '次八': '駟馬跙跙，而更其御。測曰，「駟馬跙跙」、更御乃良也。',
  '上九': '不終其德，二歲見代。測曰，「不終」之「代」、不可長也。'},

2112:{'卦': {'斷': '陽氣彊內而剛外，動而能有斷決。'},
  '初一': '斷心滅斧，冥其繩矩。測曰，「斷心滅斧」、內自治也。',
  '次二': '冥斷否，在塞耳。測曰，「冥斷否」、中心疑也。',
  '次三': '決其聾𪖣，利以治穢。測曰，「決其聾𪖣」、利有謀也。',
  '次四': '斷我否，食非，其有恥。測曰，「斷我否」、食可恥也。',
  '次五': '大腹決，其股脫，君子有斷，小人以活。測曰，「太腹決」「脫」、斷得理也。',
  '次六': '決不決，爾仇不闊，乃後有鉞。測曰，「決不決」、辜及身也。',
  '次七': '庚斷甲，我心孔碩，乃後有鑠。測曰，「庚斷甲」、義斷仁也。',
  '次八': '勇佅之傠，盜蒙決夬。測曰，「盜蒙」之「決」、妄斷也。',
  '上九': '斧刃蛾蛾，利匠人之貞。測曰，「蛾蛾」之「斧」、利征亂也。'},
 
 2113:{'卦': {'毅': '陽氣方良，毅然敢行，物信其志。'},
  '初一': '懷威滿虛。測曰，「懷威滿虛」、道德亡也。',
  '次二': '毅于心腹，貞。測曰，「毅于心腹」、內堅剛也。',
  '次三': '戴威滿頭，君子不足，小人有餘。測曰，「戴威滿頭」、小人所長也。',
  '次四': '君子說器，其人柔且毅。測曰，「君子說器」、言有方也。',
  '次五': '不田而穀，毅于揀祿。測曰，「不田而穀」、食不當也。',
  '次六': '毅于棟柱，利安大主。測曰，「毅于棟柱」、國任彊也。',
  '次七': '觥羊之毅，鳴不類。測曰，「觥羊之毅」、言不法也。',
  '次八': '毅于禍貞，君子攸名。測曰，「毅于禍貞」、不可幽蔀也。',
  '上九': '豨毅其牙，發以張弧。測曰，「豨毅其牙」、吏所獵也。'},
 
2121:{'卦': {'裝': '陽氣雖大用事，微陰據下，裝而欲去。'},
  '初一': '幽裝，莫見之行。測曰，「幽裝」「莫見」、心已外也。',
  '次二': '𪃿鵝慘于冰，翼彼南風，內懷其乘。測曰，「𪃿鵝」之「慘」、懷憂無快也。',
  '次三': '往其志，或承之喜。測曰，「往其志」、遇所快也。',
  '次四': '鶤雞朝飛，踤于北，嚶嚶相和，不輟食。測曰，「鶤雞朝飛」、何足賴也。',
  '次五': '鴻裝于淄，飲食頤頤。測曰，「鴻裝于淄」、大將得志也。',
  '次六': '經衢，周九路，不限其行賈。測曰，「經六衢」、商旅事也。',
  '次七': '裝無㒧，利征，咎。測曰，「裝無㒧」、禍且至也。',
  '次八': '季仲播軌，泣于之道，用送厥往。測曰，「季仲播軌」、送其死也。',
  '上九': '裝于昏。測曰，「裝于昏」、尚可避也。'},
 
 2122:{'卦': {'眾': '陽氣信高懷齊，萬物宣明嫭大眾多。'},
  '初一': '冥兵始，火入耳，農輟馬穀，尸將班于田。測曰，「冥兵」之「始」、始則不臧也。',
  '次二': '兵無刃，師無陳，麟或賓之，溫。測曰，「兵無刃」、德服無方也。',
  '次三': '軍或纍車，丈人摧孥，內蹈之瑕。測曰，「軍或纍車」、廟戰內傷也。',
  '次四': '虎虓振廞，豹勝其祕否。測曰，「虎虓振廞」、如鷹之揚也。',
  '次五': '躆戰喈喈，若熊若螭。測曰，「躆戰喈喈」、恃力作王也。',
  '次六': '大兵雷霆，震其耳，維用詘腹。測曰，「大兵雷霆」、威震無疆也。',
  '次七': '旌旗絓羅，干戈蛾蛾，師孕言之，哭且䁲。測曰，「旌旗絓羅」、大恨民也。',
  '次八': '兵衰衰，見其病，不見輿尸。測曰，「兵衰衰」、不血刃也。',
  '上九': '斧刃缺，其柯折，可以止，不可以伐，往血。測曰，「刃缺」「柯折」、將不足往也。'},
  
 2131:{'卦': {'親': '陽方仁愛，全真敦篤，物咸親睦。'},
  '初一': '親非其膚，其志齟齬。測曰，「親非其膚」、中心閑也。',
  '次二': '孚其肉，其志資戚。測曰，「孚其肉」、人莫聞也。',
  '次三': '螟蛉不屬，蜾臝取之，不迓侮。測曰，「螟蛉不屬」、失其體也。',
  '次四': '賓親于禮，飲食几几。測曰，「賓親于禮」、賓主偕也。',
  '次五': '厚不厚，比人將走。測曰，「厚不厚」、失類無方也。',
  '次六': '厚厚，君子秉斗。測曰，「厚厚」「君子」、得人無彊也。',
  '次七': '高亢其位，庳於同事。測曰，「位高」「事卑」、德不能也。',
  '次八': '肺附乾餱，其幹已良，君子攸行。測曰，「肺附」之「行」、不我材也。',
  '上九': '童親不貞。測曰，「童親不貞」、還自荄也。'},
 
 2132:{'卦': {'斂': '陽氣大滿於外，微陰小斂於內。'},
  '初一': '小斂不貸，利用安人正國。測曰，「小斂不貸」、其道當也。',
  '次二': '墨斂韱韱，𥧲我匪貞。測曰，「墨斂韱韱」、非所以光也。',
  '次三': '見小勿用，以我扶䟽。測曰，「見小勿用」、俟我大也。',
  '次四': '斂利小刑，小進大退。測曰，「斂利小刑」、其正退也。',
  '次五': '畜槃而衍，繭純于田。測曰，「畜槃」「繭純」、不奪時也。',
  '次六': '閔而緜而，作大元而，小人不戒。測曰，「閔緜」之「戒」、不識微也。',
  '次七': '夫牽于車，妻為剝荼，利于王姑，不利公家，病。測曰，「牽牛」「剝荼」、斂之資也。',
  '次八': '大斂大巔。測曰，「大斂」之「巔」、所斂非也。',
  '上九': '斂于時，利圉極菑。測曰，「斂于時」、奚可幾也。'},
 
 2133:{'卦': {'彊': '陽氣統剛，乾乾，萬物莫不彊梁。'},
  '初一': '彊中否貞，無攸用。測曰，「彊中否貞」、不可與謀也。',
  '次二': '鳳鳥于飛，脩其羽；君子于辰，終莫之圉。測曰，「鳳鳥于飛」、君子得時也。',
  '次三': '柱不中，梁不隆，大廈微。測曰，「柱不中」、不能正基也。',
  '次四': '爰聰爰明，左右橿橿。測曰，「爰聰爰明」、庶士方來也。',
  '次五': '君子彊梁以德，小人彊梁以力。測曰，「小人彊梁」、得位益尤也。',
  '次六': '克我彊梁，于天無疆。測曰，「克我彊梁」、大美無基也。',
  '次七': '金剛肉柔，血流于田。測曰，「金剛肉柔」、法太傷也。',
  '次八': '彊其衰，勉其弱。測曰，「彊其衰」、勉自彊也。',
  '上九': '太山拔，梁柱折，其人顛且蹶。測曰，「山拔」「梁折」、終以猛也。'},
 
 2211:{'卦': {'睟': '陽氣𥘩睟清明，物咸重光，保厥昭陽。'},
  '初一': '睟于內，清無穢。測曰，「睟于內」、清無穢也。',
  '次二': '冥駮冒睟，𥅡于中。測曰，「冥駮冒睟」、中自瘱也。',
  '次三': '目上于天，耳下于淵，恭。測曰，「目上」「耳下」、聰察極也。',
  '次四': '小人慕睟，失祿貞。測曰，「小人慕睟」、道不得也。',
  '次五': '睟于幽黃，元貞無方。測曰，「睟于幽黃」、正地則也。',
  '次六': '大睟承愆，易。測曰，「大睟承愆」、小人不克也。',
  '次七': '睟辰愆，君子補愆。測曰，「睟辰愆」、善補過也。',
  '次八': '睟惡無善。測曰，「睟惡無善」、終不可佐也。',
  '上九': '睟終永初，貞。測曰，「睟終」之「貞」、誠可嘉也。'},
 
 2212:{'卦': {'盛': '陽氣隆盛充塞，物窴然盡滿厥意。'},
  '初一': '盛不墨，失冥德。測曰，「盛不墨」、中不自克也。',
  '次二': '作不恃，克大有。測曰，「作不恃」、稱玄德也。',
  '次三': '懷利滿匈，不利于公。測曰，「懷利滿匈」、營私門也。',
  '次四': '小盛臣臣，大人之門。測曰，「小盛臣臣」、事仁賢也。',
  '次五': '何福滿肩，提禍撣撣。測曰，「何福」「提禍」、小人之道也。',
  '次六': '天錫之光，大開之疆，于謙有慶。測曰，「天錫之光」、謙大有也。',
  '次七': '乘火寒泉至。測曰，「乘火泉至」、禍不遠也。',
  '次八': '挹于滿熒，幾後之傾。測曰，「挹于滿」、幾危也。',
  '上九': '極盛不救，禍降自天。測曰，「極盛不救」、天道反也。'},
 
 2213:{'卦': {'居': '陽方躆膚赫赫，為物城郭，物咸得度。'},
  '初一': '匪譽匪咎，克守厥家。測曰，「匪譽匪咎」、其道常也。',
  '次二': '家無壺，婦承之姑，或洗之塗。測曰，「家無壺」、無以相承也。',
  '次三': '長幼序序，子克父。測曰，「子克父」、乃能有興也。',
  '次四': '見豕在堂，狗繫之迒。測曰，「見豕在堂」、其體不慶也。',
  '次五': '舳艫調安，利富貞。測曰，「舳艫」安和、順其疆也。',
  '次六': '外其井竈，三歲見背。測曰，「外其井竈」、三歲不享也。',
  '次七': '老夫擐車，少女提壺，利考家。測曰，「老父擐車」、其體乃莊也。',
  '次八': '反其几，雙其牝，几家不旨。測曰，「反几」「雙牝」、家用不臧也。',
  '上九': '株生蘖，其種不絕。測曰，「株生蘖」、其類乃長也。'},
 
 2221:{'卦': {'法': '陽高縣厥法，物仰其墨，莫不被則。'},
  '初一': '造法不法。測曰，「造法不法」、不足用也。',
  '次二': '摹法以中，克。測曰，「摹法以中」、眾之所共也。',
  '次三': '準繩不甫，亡其規矩。測曰，「準繩不甫」、其用爽也。',
  '次四': '準繩規矩，莫違我施。測曰，「準繩規矩」、由身行也。',
  '次五': '繘陸陸，缾窴腹，井潢洋，終不得食。測曰，「缾窴腹」、非學方也。',
  '次六': '于紀于綱，示以貞光。測曰，「于紀于綱」、大統明也。',
  '次七': '密網離于淵，不利于鱗。測曰，「密網離淵」、苛法張也。',
  '次八': '正彼有辜，格我無邪。測曰，「正彼有辜」、敺而至也。',
  '上九': '井無幹，水直衍，匪谿匪谷，終于愆。測曰，「井無幹」、法妄恣也。'},
 
 2222:{'卦': {'應': '陽氣極于上，陰信萌乎下，上下相應。'},
  '初一': '六幹羅如，五枝離如。測曰，「幹羅如」、附離君也。',
  '次二': '上歷施之，下律和之，非則否。測曰，「上施」「下和」、匪其肯也。',
  '次三': '一從一橫，天網䍚䍚。測曰，「一從一橫」、經緯陳也。',
  '次四': '援我罘罟，絓羅于野至。測曰，「援我罘罟」、不能以仁也。',
  '次五': '龍翰于天，貞栗其鱗。測曰，「龍翰」之「栗」、極懼墜也。',
  '次六': '熾承于天，冰萌于地。測曰，「承天」「萌地」、陽始退也。',
  '次七': '日彊其衰，應蕃貞。測曰，「日彊其衰」、惡敗類也。',
  '次八': '極陽徵陰，不移日而應。測曰，「極陽徵陰」、應其發也。',
  '上九': '元離之極，君子應以大稷。測曰，「元離之極」、不可遏止也。'},
 
 2223:{'卦': {'迎': '陰氣成形乎下，物咸遡而迎之。'},
  '初一': '迎他匪，無貞有邪。測曰，「迎他匪應」、非所與并也。',
  '次二': '蛟潛於淵，陵卵化之；人或陰言，百姓和之。測曰，「蛟潛」之「化」、中精誠也。',
  '次三': '精微往來，妖先靈覺。測曰，「精微往來」、妖咎徵也。',
  '次四': '裳有衣襦，男子目珠，婦人啑鉤，貞。測曰，「裳有衣襦」、陰感陽也。',
  '次五': '黃乘否貞。測曰，「黃乘否貞」、不可與朋也。',
  '次六': '玄黃相迎，其意感感。測曰，「玄黃相迎」、以類應也。',
  '次七': '遠之𥅠，近之棓，迎父迦逅。測曰，「遠𥅠」「近棓」、失父類也。',
  '次八': '見血入門，捬迎中廷。測曰，「見血入門」、以賢自衛也。',
  '上九': '濕迎床足，罦于牆屋。測曰，「濕迎床足」、願在內也。'},
 
 2231:{'卦': {'遇': '陰氣始來，陽氣始往，往來相逢。'},
  '初一': '幽遇神及師，夢貞。測曰，「幽遇神」、思得理也。',
  '次二': '衝衝兒遇，不受定之諭。測曰，「衝衝兒遇」、不肖子也。',
  '次三': '不往來，不求得，士女之貞。測曰，「不往」「不求」、士女則也。',
  '次四': '㒁㒁兌人，遇雨厲。測曰，「兌人」「遇雨」、還自賊也。',
  '次五': '田遇禽，人莫之禁。測曰，「田遇禽」、誠可勉也。',
  '次六': '俾蛛罔罔，遇螽，利雖大不得從。測曰，「蛛」之「罔」、害不遠也。',
  '次七': '振其角，君父遇辱，匪正命。測曰，「振其角」、直道行也。',
  '次八': '兩兕鬭，一角亡，不勝喪。測曰，「兩兕鬭」、亡角喪也。',
  '上九': '觝其角，遇下毀足。測曰，「觝其角」、何可當也。'},
 
 2232:{'卦': {'竈': '陰雖沃而灑之，陽猶執而龢之。'},
  '初一': '竈無實，乞于鄰。測曰，「竈無」、有虛名也。',
  '次二': '黃鼎介，其中裔，不飲不食，孚無害。測曰，「黃鼎介」、中廉貞也。',
  '次三': '竈無薪，黃金瀕。測曰，「竈無薪」、有不用也。',
  '次四': '鬲實之食，得其勞力。測曰，「鬲實之食」、時我奉也。',
  '次五': '鼎犬可觴，不齊不莊。測曰，「鼎大可觴」、饗無意也。',
  '次六': '五味龢，調如美如，大人之饗。測曰，「味龢」之「饗」、宰輔事也。',
  '次七': '脂牛正肪，不濯釜而烹，敗歐歍之疾至。測曰，「脂牛」「歐歍」、不絜志也。',
  '次八': '食其委，雖噭不毀。測曰，「食其委」、蒙厥德也。',
  '上九': '竈滅其火，唯家之禍。測曰，「竈滅其火」、國之賊也。'},
 
 2233:{'卦': {'大': '陰虛其內，陽逢其外，物與盤蓋。'},
  '初一': '淵潢洋，包無方，冥。測曰，「淵潢洋」、資裹無方也。',
  '次二': '大其慮，躬自鑢。測曰，「大其慮」、為思所傷也。',
  '次三': '大不大，利以成大。測曰，「大不大」、以小作基也。',
  '次四': '大其門郊，不得其刀，鳴虛。測曰，「大其門郊」、實去名來也。',
  '次五': '包荒以中，克。測曰，「包荒以中」、督九夷也。',
  '次六': '大失小，多失少。測曰，「大失小」、禍由微也。',
  '次七': '大奢迂，自削以觚，或益之餔。測曰，「奢迂」「自削」、能自非也。',
  '次八': '豐牆峭阯，三歲不築，崩。測曰，「豐牆」之「峭」、崩不遟也。',
  '上九': '大終以蔑，否出天外。測曰，「大終以蔑」、小為大質也。'},
 
 2311:{'卦': {'廓': '陰氣瘱而𢙅之，陽猶恢而廊之。'},
  '初一': '廓之恢之，不正其基。測曰，「廓之恢之」、始基傾也。',
  '次二': '金榦玉楨，廓于城。測曰，「金榦玉楨」、蕃輔正也。',
  '次三': '廓無子，室石婦。測曰，「廓無子」、焉得後生也。',
  '次四': '恢其門戶，以御寇虜。測曰，「恢其門戶」、大經營也。',
  '次五': '天門大開，恢堂之階，或生之差。測曰，「天門大開」、德不能滿堂也。',
  '次六': '維豐維崇，百辟馮馮，伊德攸興。測曰，「維豐維崇」、茲太平也。',
  '次七': '外大扢，其中失，君子至野，小人入室。測曰，「外大扢」、中無人也。',
  '次八': '廓其外，虛其內，利鼓鉦。測曰，「廓外」「虛內」、乃能有聞也。',
  '上九': '極廓于高庸，三歲無童。測曰，「極廓高庸」、終無所臣也。'},
 
 2312:{'卦': {'文': '陰斂其質，陽散其文，文質班班，萬物粲然。'},
  '初一': '袷䙡何縵，玉貞。測曰，「袷䙡何縵」、文在內也。',
  '次二': '文蔚質否。測曰，「文蔚質否」、不能俱睟也。',
  '次三': '大文彌樸，孚似不足。測曰，「大文彌樸」、質有餘也。',
  '次四': '裴如邠如，虎豹文如，匪天之享，否。測曰，「斐邠」之「否」、奚足譽也。',
  '次五': '炳如彪如，尚文昭如，車服庸如。測曰，「彪如」在上、天文炳也。',
  '次六': '鴻文無范，恣于川。測曰，「鴻文無范」、恣意往也。',
  '次七': '雉之不祿，而雞藎穀。測曰，「雉之不祿」、難幽養也。',
  '次八': '彫韱，穀布亡于時，文則亂。測曰，「彫韱」之「文」、徒費日也。',
  '上九': '極文密密，易以黼黻。測曰，「極文」「易」、當以質也。'},
 
 2313:{'卦': {'禮': '陰在下而陽在上，上下正體，物與有禮。'},
  '初一': '履于跂，後其祖禰。測曰，「履于跂」、退其親也。',
  '次二': '目穆穆，足肅肅，乃貫以棘。測曰，「穆穆」「肅肅」、敬出心也。',
  '次三': '畫象成形，孚無成。測曰，「畫象成形」、非其真也。',
  '次四': '孔鴈之儀，利用登于階。測曰，「孔鴈之儀」、可法則也。',
  '次五': '懷其違，折其匕，過喪錫九矢。測曰，「懷違」「折匕」、貶天祿也。',
  '次六': '魚鱗差之，乃矢施之，帝用登于天。測曰，「魚鱗差之」、貴賤位也。',
  '次七': '出禮不畏，入畏。測曰，「出禮不畏」、人所棄也。',
  '次八': '冠戚䏔，履全履。測曰，「戚䏔」、明「」、不可上也。',
  '上九': '戴無首，焉用此九。測曰，「無首」之「戴」、焉所往也。'},

2321:{'卦': {'逃': '陰氣章彊，陽氣潛退，萬物將亡。'},
  '初一': '逃水之夷，滅其創迹。測曰，「逃水之夷」、跡不創也。',
  '次二': '心惕惕，足金舄，不志溝壑。測曰，「心惕惕」、義不將也。',
  '次三': '兢其股，鞭其馬，寇望其戶，逃利。測曰，「兢股」「鞭馬」、近有見也。',
  '次四': '喬木維摐，飛鳥過之，或降。測曰，「喬木」之「鳥」、欲止則降也。',
  '次五': '見鷕踤于林，獺入于淵，征。測曰，「見鷕」及「獺」、深居逃凶也。',
  '次六': '多田不婁，費我膎功。測曰，「多田不婁」、費力忘功也。',
  '次七': '見于纍，後乃克飛。測曰，「見于纍」、幾不足高也。',
  '次八': '頸加于矰，維䋚其繩。測曰，「頸加」「維䋚」、無自勞也。',
  '上九': '利逃跰跰，盜德嬰城。測曰，「盜德嬰城」、何至逃也。'},
 
 2322:{'卦': {'唐': '陰氣茲來，陽氣茲往，物且盪盪。'},
  '初一': '唐于內，勿作，厲。測曰，「唐于內」、無執守也。',
  '次二': '唐處冥，利用東征。測曰，「唐冥」之「利」、利明道也。',
  '次三': '唐素不貞，亡彼瓏玲。測曰，「亡彼瓏玲」、非爾所也。',
  '次四': '唐無適，道義之辟。測曰，「唐無適」、惟義予也。',
  '次五': '奔鹿懷鼷，得不訾。測曰，「奔鹿懷鼷」、奚足功也。',
  '次六': '唐不獨足，代天班祿。測曰，「唐不獨足」、無私容也。',
  '次七': '弋彼三飛，明明于征，終日不歸，亡。測曰，「弋彼三飛」、適無所從也。',
  '次八': '唐收祿，社鬼輟哭，或得其沐。測曰，「唐收祿」、復亡也。',
  '上九': '明珠彈于飛肉，其得不復。測曰，「明珠彈肉」、費不當也。'},
 
 2323:{'卦': {'常': '陰以知臣，陽以知辟，君臣之道，萬世不易。'},
  '初一': '戴神墨，履靈式，以一耦萬，終不稷。測曰，「戴神墨」、體一形也。',
  '次二': '內常微，女貞厲。測曰，「內常微」、女不正也。',
  '次三': '日常其德，三歲不食。測曰，「日常其德」、君道明也。',
  '次四': '月不常，或失之行。測曰，「月不常」、臣失行也。',
  '次五': '其從其橫，天地之常。測曰，「其從其橫」、君臣常也。',
  '次六': '得七而九，懦撓其剛，不克常。測曰，「得七而九」、棄盛乘衰也。',
  '次七': '淊淊往來，有常衰如，克承貞。測曰，「淊淊往來」、以正承非也。',
  '次八': '常疾不疾，咎成不詰。測曰，「常疾不疾」、不能自治也。',
  '上九': '疾其疾，巫毉不失。測曰，「疾其疾」、能自毉也。'},
 
 2331:{'卦': {'度': '陰氣日躁，陽氣日舍，躁躁舍舍，各得其度。'},
  '初一': '中度獨失。測曰，「度獨失」、不能有成也。',
  '次二': '澤不舍，冥中度。測曰，「澤不舍」、乃能有正也。',
  '次三': '小度差差，大櫴之階。測曰，「小度」之「差」、大度傾也。',
  '次四': '榦楨，利干城。測曰，「榦楨」之「利」、利經營也。',
  '次五': '榦不榦，櫴于營。測曰，「幹不幹」、不能有寧也。',
  '次六': '大度檢檢，于天示象，垂其范。測曰，「大度檢檢」、垂象貞也。',
  '次七': '不度規之，鬼即訾之。測曰，「不度規之」、明察笑也。',
  '次八': '石赤不奪，節士之必。測曰，「石赤不奪」、可與有要也。',
  '上九': '積善之貸，十年不復。測曰，「積善之貸」、不得造也。'},
 
 2332:{'卦': {'永': '陰以武取，陽以文與，道可長久。'},
  '初一': '不替不爽，長子之常。測曰，「不替不爽」、永宗道也。',
  '次二': '內懷替爽，永失貞祥。測曰，「內懷替爽」、安可久也。',
  '次三': '永其道，未得無咎。測曰，「永其道」、誠可保也。',
  '次四': '子序不序，先賓永失主。測曰，「子序不序」、非永方也。',
  '次五': '三綱得于中極，天永厥福。測曰，「三綱」之「永」、其道長也。',
  '次六': '大永于福，反虛庭，入奠冥。測曰，「大永于福」、福反亡也。',
  '次七': '老木生蒔，永以纏其所無。測曰，「老木生蒔」、永厥體也。',
  '次八': '永不軌，凶亡流于後。測曰，「永不軌」、其命劑也。',
  '上九': '永終馴首。測曰，「永終馴首」、長愷悌也。'},
 
 2333:{'卦': {'昆': '陰將離之，陽尚昆之，昆道尚同。'},
  '初一': '昆于黑，不知白。測曰，「昆于黑」、不可謂人也。',
  '次二': '白黑菲菲，三禽一角同尾。測曰，「三禽一角」、無害心也。',
  '次三': '昆于白，失不黑，無除，一尾三角。測曰，「昆白」「不黑」、不相親也。',
  '次四': '鳥託巢于叢，人寄命于公。測曰，「鳥託巢」、公無貧也。',
  '次五': '穀不穀，失䟽數，眾氂毀玉。測曰，「穀失䟽數」、奚足旬也。',
  '次六': '昆干井市，文車同軌。測曰，「昆于井市」、同一倫也。',
  '次七': '蓋徧不覆，晏雨不救。測曰，「蓋徧不覆」、德不均也。',
  '次八': '昆于危難，乃覆之安。測日，「危難」之「安」、素施仁也。',
  '上九': '昆于死，棄寇遺。測曰，「昆于死」、棄厥身也。'},
 
 3111:{'卦': {'減': '陰氣息陽氣消，陰盛陽衰，萬物以微。'},
  '初一': '善減不減，冥。測曰，「善減不減」、常自沖也。',
  '次二': '心減自中，以形于身。測曰，「心減」「形身」、困諸中也。',
  '次三': '減其儀，利用光于階。測曰，「減其儀」、欲自禁也。',
  '次四': '減於乂，貶其位。測曰，「減於乂」、無以莅眾也。',
  '次五': '減其黃貞，不承于上，寧。測曰，「減黃貞」、臣道丁也。',
  '次六': '幽闡積，不減不施，石。測曰，「幽闡」「不施」、澤不平也。',
  '次七': '減其疾，損其䘏，厲不至。測曰，「減其疾」、不至危也。',
  '次八': '瀏漣漣，減于生根。測曰，「瀏漣」之「減」、生根毀也。',
  '上九': '減終，利用登于西山，臨于大川。測曰，「減終」之「登」、誠可為也。'},
 
 3112:{'卦': {'唫': '陰不之化，陽不之施，萬物各唫。'},
  '初一': '唫不予，丈夫婦處。測曰，「唫不予」、人所違也。',
  '次二': '唫于血，資乾骨。測曰，「唫于血」、䂄自肥也。',
  '次三': '貌不交，唭㘈，唫無辭。測曰，「貌不交」、人道微也。',
  '次四': '唫其穀，不振不俗，纍老及族。測曰，「唫其穀」、不得相希也。',
  '次五': '不中不督，腐蠹之嗇。測曰，「不中不督」、其唫非也。',
  '次六': '泉源洋洋，唫于丘園。測曰，「泉源」之「唫」、不可譏也。',
  '次七': '唫于體，黃肉毀。測曰，「唫于體」、骨肉傷也。',
  '次八': '唫遇禍，禱以牛，解。測曰，「唫遇禍」、大費當也。',
  '上九': '唫不雨，孚乾脯。測曰，「唫不雨」、何可望也。'},
 
 3113:{'卦': {'守': '陰守戶，陽守門，物莫相干。'},
  '初一': '閉朋牖，守元有。測曰，「閉朋牖」、善持有也。',
  '次二': '迷自守，不如一之有。測曰，「迷自守」、中無所以也。',
  '次三': '無喪無得，往來默默。測曰，「無喪無得」、守厥故也。',
  '次四': '象艮有守。測曰，「象艮」之「守」、廉無怙也。',
  '次五': '守中以和，要侯貞。測曰，「守中以和」、侯之素也。',
  '次六': '車案軔，圭璧塵。測曰，「車案軔」、不接鄰也。',
  '次七': '群陽不守，男子之貞。測曰，「群陽」之「守」、守貞信也。',
  '次八': '臼無杵，其碓舉，天陰不雨，白日毀暑。測曰，「臼無杵」、其守貧也。',
  '上九': '與荼有守，辭于盧首，不殆。測曰，「與荼有守」、故愈新也。'},
 
 3121:{'卦': {'翕': '陰來逆變，陽往順化，物退降集。'},
  '初一': '狂衝于冥，翕其志。雖欲逍遙，天不之茲。測曰，「狂衝于冥」、天未與也。',
  '次二': '翕冥中，射貞。測曰，「翕冥中」、正予也。',
  '次三': '翕食嘬嘬。測曰，「翕食嘬嘬」、利如舞也。',
  '次四': '翕其羽，利用舉。測曰，「翕其羽」、朋友助也。',
  '次五': '翕其腹，辟金穀。測曰，「翕其腹」、非所以舉也。',
  '次六': '黃心鴻翼，翕于天。測曰，「黃心鴻翼」、利得輔也。',
  '次七': '翕繳惻惻。測曰，「翕繳惻惻」、被離害也。',
  '次八': '揮其罦，絕其羂，殆。測曰，「揮罦」「絕羂」、危得遂也。',
  '上九': '㩣其角，維用抵族。測曰，「㩣其角」、殄厥類也。'},
 
 3122:{'卦': {'聚': '陰氣收聚，陽不禁禦，物相崇聚。'},
  '初一': '鬼神以無靈。測曰，「鬼神無靈」、形不見也。',
  '次二': '燕聚嘻嘻。測曰，「燕聚嘻嘻」、樂淫衍也。',
  '次三': '宗其高年，群鬼之門。測曰，「宗其高年」、鬼待敬也。',
  '次四': '牽羊示于叢社，執圭信辟其左股，野。測曰，「牽羊于叢」、不足勞也。',
  '次五': '鼎血之蕕，九宗之好，乃後有孚。測曰，「鼎血之蕕」、信王命也。',
  '次六': '畏其鬼，尊其禮，狂作昧，淫亡。測曰，「畏鬼」之「狂」、過其正也。',
  '次七': '竦萃于丘冢。測曰，「竦萃丘冢」、禮不廢也。',
  '次八': '鴟鳩在林，㕹彼眾禽。測曰，「鴟鳩在林」、眾所㕹也。',
  '上九': '垂涕纍鼻，聚家之彙。測曰，「垂涕纍鼻」、時命絕也。'},
 
 
 3123:{'卦': {'積': '陰將大閉，陽尚小開，山川藪澤，萬物攸歸。'},
  '初一': '冥積否，作明基。測曰，「冥積否」、在惡也。',
  '次二': '積不用而至于大用，君子介心。測曰，「積不用」、不可規度也。',
  '次三': '積石不食，費其勞力。測曰，「積石不食」、無可獲也。',
  '次四': '君子積善，至于車耳。測曰，「君子積善」、至于蕃也。',
  '次五': '藏不滿，盜不嬴。測曰，「藏滿」「盜嬴」、還自損也。',
  '次六': '大滿碩施，得人無亢。測曰，「大滿碩施」、人所來也。',
  '次七': '魁而顏而，玉帛班而，決欲收寇。測曰，「魁而顏而」、盜之招也。',
  '次八': '積善辰禍，維先之罪。測曰，「積善辰禍」、非己辜也。',
  '上九': '小人積非，至于苗裔。測曰，「小人積非」、禍所骫也。'},
  
 3131:{'卦': {'飾': '陰白陽黑，分行其職，出入有餙。'},
  '初一': '言不言，不以言。測曰，「言不言」、默而信也。',
  '次二': '無質餙，先文後失服。測曰，「無質」「先文」、失貞也。',
  '次三': '吐黃酋舌，拑黃聿，利見哲人。測曰，「舌」「聿」之「利」、利見知人也。',
  '次四': '利口哇哇，商人之貞。測曰，「哇哇」之「貞」、利于商也。',
  '次五': '下言如水，實以天牝。測曰，「下言」之「水」、能自沖也。',
  '次六': '言無追如，抑亦飛如，大人震風。測曰，「言無追如」、抑亦揚也。',
  '次七': '不丁言時，微于辭，見上疑。測曰，「不丁言時」、何可章也。',
  '次八': '蛁鳴喁喁，血出其口。測曰，「蛁鳴喁喁」、口自傷也。',
  '上九': '白舌于于，屈于根，君子否信。測曰，「白舌于于」、誠可長也。'},
 
 3132:{'卦': {'疑': '陰陽相磑，物咸彫離，若是若非。'},
  '初一': '疑恛恛，失貞矢。測曰，「疑恛」「失貞」、何可定也。',
  '次二': '疑自反，孚不遠。測曰，「疑自反」、反清靜也。',
  '次三': '疑彊昭，受茲閔閔，于其心祖。測曰，「疑彊昭」、中心冥也。',
  '次四': '疑考舊，遇貞孚。測曰，「疑考舊」、先問也。',
  '次五': '𧋒黃，疑金中。測曰，「𧋒黃疑中」、邪奪正也。',
  '次六': '誓貞可聽，疑則有誠。測曰，「誓貞可聽」、明王命也。',
  '次七': '鬼魂疑貞厲，嚘嗚，戈木之烏，射穴之狐，反自耳，厲。測曰，「鬼魂」之「疑」、誠不可信也。',
  '次八': '顛疑遇幹客，三歲不射。測曰，「顛疑遇客」、甚足敬也。',
  '上九': '九疑無信，控弧擬麋，無。測曰，「九疑無信」、終無所名也。'},
 
 3133:{'卦': {'視': '陰成魄，陽成妣，物之形貌咸可視。'},
  '初一': '內其明，不用其光。測曰，「內其明」、自窺深也。',
  '次二': '君子視內，小人視外。測曰，「小人視外」、不能見心也。',
  '次三': '視其德，可以幹王之國。測曰，「視德」之「幹」、乃能有全也。',
  '次四': '粉其題䪻，雨其渥須，視無姝。測曰，「粉題」「雨須」、不可忍瞻也。',
  '次五': '鸞鳳紛如，厥德暉如。測曰，「鸞鳳紛如」、德光皓也。',
  '次六': '素車翠蓋，維視之害貞。測曰，「素車翠蓋」、徒好外也。',
  '次七': '視其瑕，無穢。測曰，「視其瑕」、能自矯也。',
  '次八': '翡翠于飛，離其翼；狐鼦之毛，躬之賊。測曰，「翡翠」「狐鼦」、好作咎也。',
  '上九': '日沒其光，賁于東方，用視厥始。測曰，「日沒」「賁東」、終顧始也。'},
 
 3211:{'卦': {'沈': '陰懷于陽，陽懷于陰，志在玄宮。'},
  '初一': '沈耳于閨，不聞貞。測曰，「沈耳于閨」、失德體也。',
  '次二': '沈視自見，賢於眇之眄。測曰，「沈視」之「見」、得正美也。',
  '次三': '沈于美，失貞矢。測曰，「沈于矢」、作聾盲也。',
  '次四': '宛雛沈視，食苦貞。測曰，「宛雛沈視」、擇食方也。',
  '次五': '雕鷹高翔，沈其腹，好𡢘惡粥。測曰，「雕鷹高翔」、在腐糧也。',
  '次六': '見票如累，明，利以正于王。測曰，「見票如累」、其道明也。',
  '次七': '離如婁如，赤肉鴟梟，厲。測曰，「離婁」「赤肉」、食不臧也。',
  '次八': '盼得藥，利征。測曰，「盼得其藥」、利征邁也。',
  '上九': '血如剛，沈于顙，前尸後喪。測曰，「血剛」「沈顙」、終以貪敗也。'},
 
 3212:{'卦': {'內': '陰去其內而在于外，陽去其外而在乎內，萬物之既。'},
  '初一': '謹于媐㐜，初貞後寧。測曰，「謹于媐㐜」、始女貞也。',
  '次二': '邪其內主，迂彼黃床。測曰，「邪其內主」、遠乃寧也。',
  '次三': '爾儀而悲，坎我西階。測曰，「爾儀而悲」、代母情也。',
  '次四': '好小好危，喪其蘊袍，厲。測曰，「好小好危」、不足榮也。',
  '次五': '龍下于泥，君子利用取媐，遇庸夷。測曰，「龍下于泥」、陽下陰也。',
  '次六': '黃昏于飛，內其羽，雖欲滿宮，不見其女。測曰，「黃昏」「內羽」、不能自禁也。',
  '次七': '枯垣生莠，皬頭內其雉婦，有。測曰，「枯垣生莠」、勿慶類也。',
  '次八': '內不克婦，荒家及國，涉深不可測。測曰，「內不克婦」、國之孽也。',
  '上九': '雨降于地，不得止，不得過。測曰，「雨降于地」、澤節也。'},
 
 3213:{'卦': {'去': '陽去其陰，陰去其陽，物咸倜倡。'},
  '初一': '去此靈淵，舍彼枯園。測曰，「去此靈淵」、不以謙將也。',
  '次二': '去彼枯園，舍下靈淵。測曰，「舍下靈淵」、謙道光也。',
  '次三': '高其步，之堂有露。測曰，「高步」「有露」、妄升也。',
  '次四': '去于父子，去于臣主。測曰，「去于父子」、非所望也。',
  '次五': '攓其衣，之庭有麋。測曰，「攓衣」「有麋」、亦可懼也。',
  '次六': '躬去于成，天遺厥名。測曰，「躬去于成」、讓不居也。',
  '次七': '去其德貞，三死不令。測曰，「去其德貞」、終死醜也。',
  '次八': '月高弦，火幾縣，不可以動，動有愆。測曰，「月弦火縣」、恐見咎也。',
  '上九': '求我不得，自我西北。測曰，「求我不得」、安可久也。'},
 
 3221:{'卦': {'晦': '陰登于陽，陽降于陰，物咸喪明。'},
  '初一': '同冥獨見，幽貞。測曰，「同冥獨見」、中獨昭也。',
  '次二': '盲征否。測曰，「盲征否」、明不見道也。',
  '次三': '陰行陽從，利作不凶。測曰，「陰行陽從」、事必外也。',
  '次四': '晦其類，失金匱。測曰，「晦其類」、法度廢也。',
  '次五': '日正中，月正隆，君子自晦不入窮。測曰，「日中」「月隆」、明恐挫也。',
  '次六': '玄鳥維愁，明降于幽。測曰，「玄鳥維愁」、將下昧也。',
  '次七': '䏴提明德，或遵之行。測曰，「䏴提明」、德將遵行也。',
  '次八': '視非其直，夷其右目，滅國喪家。測曰，「視非」「夷目」、國以喪也。',
  '上九': '晦冥冥，利于不明之貞。測曰，「晦冥」之「利」、不得獨明也。'},
 
 3222:{'卦': {'瞢': '陰征南，陽征北，物失明貞，莫不瞢瞢。'},
  '初一': '瞢復睒天，不覩其軫。測曰，「瞢復睒天」、無能見也。',
  '次二': '明復睒天，覩其根。測曰，「明復睒天」、中獨爛也。',
  '次三': '師或導射，豚其埻。測曰，「師或導射」、無以辨也。',
  '次四': '鑒貞不迷，于人攸資。測曰，「鑒貞不迷」、誠可信也。',
  '次五': '倍明仮光，觸蒙昏。測曰，「倍明仮光」、人可頻也。',
  '次六': '瞢瞢之離，不宜熒且𡛖。測曰，「瞢瞢之離」、中薆薆也。',
  '次七': '瞢好，明其所惡。測曰，「瞢好」之「惡」、著不可昧也。',
  '次八': '昏辰利于月，小貞未及星。測曰，「昏辰利月」、尚可願也。',
  '上九': '時𨲠𨲠，不獲其嘉，男子折笄，婦人易哿。測曰，「不獲其嘉」、男死婦歎也。'},
 
 3223:{'卦': {'窮': '陰氣塞宇，陽亡其所，萬物窮遽。'},
  '初一': '窮其窮，而民好中。測曰，「窮其窮」、情在中也。',
  '次二': '窮不窮，而民不中。測曰，「窮不窮」、詐可隆也。',
  '次三': '窮思達。測曰，「窮思達」、師在心也。',
  '次四': '土不和，木科橢。測曰，「土不和」、病乎民也。',
  '次五': '羹無糝，其腹坎坎，不失其範。測曰，「羹無糝」、猶不失其正也。',
  '次六': '山無角，水無鱗，困犯身。測曰，「山無角」、困百姓也。',
  '次七': '正其足，蹛于狴獄，三歲見錄。測曰，「正其足」、險得平也。',
  '次八': '涉于霜雪，纍項于䣛。測曰，「纍項于䣛」、亦不足生也。',
  '上九': '破璧毀圭，曰竈生鼃，天禍以他。測曰，「破璧毀圭」、逢不幸也。'},
 
 3231:{'卦': {'割': '陰氣割物，陽形縣殺，七日幾絕。'},
  '初一': '割其耳目，及其心腹，厲。測曰，「割其耳目」、中無外也。',
  '次二': '割其肬贅，利以無穢。測曰，「割其肬贅」、惡不得大也。',
  '次三': '割鼻食口，喪其息主。測曰，「割鼻」「喪主」、損無榮也。',
  '次四': '宰割平平。測曰，「宰割平平」、能有成也。',
  '次五': '割其股肱，喪其服馬。測曰，「割其股肱」、亡大臣也。',
  '次六': '割之無創，飽于四方。測曰，「割之無創」、道可分也。',
  '次七': '紫蜺矞雲，朋圍日，其疾不割。測曰，「紫蜺矞雲」、不知刊也。',
  '次八': '割其蠹，得必疾。測曰，「割其蠹」、國所便也。',
  '上九': '割肉取骨，滅頂于血。測曰，「割肉」「滅血」、不能自全也。'},
 
 3232:{'卦': {'止': '陰大止物於上，陽亦止物於下，下上俱止。'},
  '初一': '止于止，內明無咎。測曰，「止于止」、智足明也。',
  '次二': '車軔俟，馬酋止。測曰，「車軔」「馬止」、不可以行也。',
  '次三': '關其門戶，用止狂蠱。測曰，「關其門戶」、禦不當也。',
  '次四': '止于童木，求其䟽穀。測曰，「止于童木」、求其窮也。',
  '次五': '柱奠廬，蓋蓋車，穀均䟽。測曰，「柱」及「蓋」「穀」、貴中也。',
  '次六': '方輪廣軸，坎軻其輿。測曰，「方輪」「坎軻」、還自震也。',
  '次七': '車纍其俿，馬獵其蹄，止貞。測曰，「車纍」「馬獵」、行可鄰也。',
  '次八': '弓善反，弓惡反，善馬恨，惡馬恨，絕弸破車，終不偃。測曰，「弓反」「馬恨」、終不可以也。',
  '上九': '折于株木，輆于砭石，止。測曰，「折木」「輆石」、君子所止也。'},
 
 3233:{'卦': {'堅': '陰形胼冒，陽喪其緒，物競堅彊。'},
  '初一': '磐石固內，不化貞。測曰，「磐石固內」、不可化也。',
  '次二': '堅白玉形，內化貞。測曰，「堅白玉形」、變可為也。',
  '次三': '堅不凌，或泄其中。測曰，「堅不凌」、不能持齊也。',
  '次四': '小螽營營，螮其蛡蛡，不介在堅螮。測曰，「小螽營營」、固其氐也。',
  '次五': '蛡大螮小，虛。測曰，「蛡大螮小」、國虛空也。',
  '次六': '韱螮紗紗，縣于九州。測曰，「韱螮」之「縣」、民以康也。',
  '次七': '堅顛觸冢。測曰，「堅顛觸冢」、不知所行也。',
  '次八': '怙堅禍，惟用解𧋈之貞。測曰，「怙堅禍」、用直方也。',
  '上九': '螽焚其翊，喪于尸。測曰，「螽焚其翊」、所憑喪也。'},

3311:{'卦': {'成': '陰氣方消，陽藏於靈，物濟成形。'},
  '初一': '成若否，其用不已，冥。測曰，「成若否」、所以不敗也。',
  '次二': '成微改改，未成而殆。測曰，「成微改改」、不能自遂也。',
  '次三': '成躍以縮，成飛不逐。測曰，「或躍以縮」、成德壯也。',
  '次四': '將成矜敗。測曰，「將成」之「矜」、成道病也。',
  '次五': '中成獨督，大。測曰，「中成獨督」、能處中也。',
  '次六': '成魁瑣，以成獲禍。測曰，「成」之「魁瑣」、不以讓也。',
  '次七': '成闕，補。測曰，「成闕」之「補」、固難承也。',
  '次八': '時成不成，天降亡貞。測曰，「時成不成」、獨失中也。',
  '上九': '成窮入于敗，毀成，君子不成。測曰，「成窮」以「毀」、君子以終也。'},
 
3312:{'卦': {'䦯': '陰陽交跌，相闔成一，其禍泣萬物。'},
  '初一': '圜方杌棿，其內窾換。測曰，「圜方杌棿」、內相失也。',
  '次二': '䦯無間。測曰，「無間」之「䦯」、一其二也。',
  '次三': '龍襲非其穴，光亡于室。測曰，「龍襲非穴」、失其常也。',
  '次四': '臭肥滅鼻，利美貞。測曰，「滅鼻」之「貞」、沒所勞也。',
  '次五': '齧骨折齒，滿缶。測曰，「齧骨折齒」、大貪利也。',
  '次六': '飲汗吭吭，得其膏滑。測曰，「飲汗吭吭」、道足嗜也。',
  '次七': '䦯其差，前合後離。測曰，「䦯其差」、其合離也。',
  '次八': '輔其折，廅其缺，其人暉且偈。測曰，「輔折」「廅缺」、猶可善也。',
  '上九': '陰陽啟𠯒，其變赤白。測曰，「陰陽啟𠯒」、極則反也。'},
 
3313:{'卦': {'失': '陰大作賊，陽不能得，物陷不測。'},
  '初一': '刺虛滅刃。測曰，「刺虛滅刃」、深自幾也。',
  '次二': '藐德靈徵，失。測曰，「藐德」之「失」、不知畏徵也。',
  '次三': '卒而從而，䘏而竦而，于其心祖。測曰，「卒而從而」、能自改也。',
  '次四': '信過不食，至于側匿。測曰，「信過不食」、失正祿也。',
  '次五': '黃兒以中，蕃；君子以之，洗于愆。測曰，「黃兒以中」、過以洗也。',
  '次六': '滿其倉，蕪其田，食其實，不養其根。測曰，「滿倉」「蕪田」、不能脩本也。',
  '次七': '疾則藥，巫則酌。測曰，「疾藥」「巫酌」、禍可轉也。',
  '次八': '雌鳴干辰，牝角魚木。測曰，「雌鳴于辰」、厥正反也。',
  '上九': '日月之逝，改于尸。測曰，「改于尸」、尚不遠也。'},
 
3321:{'卦': {'劇': '陰窮大泣，陽無介儔，離之劇。'},
  '初一': '骨纍其肉，內幽。測曰，「骨纍其肉」、賊內行也。',
  '次二': '血出之蝕，凶貞。測曰，「血出之蝕」、君子傷之也。',
  '次三': '酒作失德，鬼睒其室。測曰，「酒作失德」、不能持也。',
  '次四': '食于劇，父母采餕，若。測曰，「食劇」以「若」、為順祿也。',
  '次五': '出野見虛，有虎牧豬，搴絝與襦。測曰，「出野見虛」、無所措足也。',
  '次六': '四國滿斯，宅。測曰，「四國滿斯」、求安宅也。',
  '次七': '麃而半而，戴禍顏而。測曰，「麃而半而」、戴禍較也。',
  '次八': '缾纍于繘，貞顇。測曰，「缾纍于繘」、厥職迫也。',
  '上九': '海水群飛，蔽于天杭。測曰，「海水群飛」、終不可語也。'},
 
 3322:{'卦': {'馴': '陰氣大順，渾沌無端，莫見其根。'},
  '初一': '黃靈幽貞，馴。測曰，「黃靈幽貞」、順以正也。',
  '次二': '𡢘其膏，女子之勞，不靜亡命。測曰，「𡢘膏」之「亡」、不能清淨也。',
  '次三': '牝貞常慈，衛其根。測曰，「牝貞常慈」、不忘本也。',
  '次四': '徇其勞，不如五之豪。測曰，「徇其勞」、伐善也。',
  '次五': '靈囊大包，其德珍黃。測曰，「靈囊大包」、不敢自盛也。',
  '次六': '囊失括，泄珍器。測曰，「囊失括」、臣口溢也。',
  '次七': '方堅犯順，利臣貞。測曰，「方堅犯順」、守正節也。',
  '次八': '馴非其正，不保厥命。測曰，「馴非其正」、無所統一也。',
  '上九': '馴義忘生，賴于天貞。測曰，「馴義忘生」、受命必也。'},
 
 3323:{'卦': {'將': '陰氣濟物乎上，陽信將復始之乎下。'},
  '初一': '將造邪，元厲。測曰，「將造邪」、危作主也。',
  '次二': '將無疵，元睟。測曰，「將無疵」、易為後也。',
  '次三': '鑪鈞否，利用止。測曰，「鑪鈞否」、化內傷也。',
  '次四': '將飛得羽，利以登于天。測曰，「將飛得羽」、其輔彊也。',
  '次五': '大爵將飛，拔其翮，毛羽雖眾，不得適。測曰，「大雀」「拔翮」、不足賴也。',
  '次六': '日失烈烈，君子將衰降。測曰，「日失烈烈」、自光大也。',
  '次七': '趹舩跋車，其害不遐。測曰，「趹舩跋車」、不遠害也。',
  '次八': '小子在淵，丈人播舩。測曰，「丈人播舩」、濟溺世也。',
  '上九': '紅蠶緣于枯桑，其繭不黃。測曰，「緣于枯桑」、蠶功敗也。'},
 
 3331:{'卦': {'難': '陰氣方難，水凝地拆，陽弱於淵。'},
  '初一': '難我冥冥。測曰，「難我冥冥」、未見形也。',
  '次二': '凍冰瀆，狂馬椯木。測曰，「狂馬椯木」、妄生也。',
  '次三': '中堅剛，難于非常。測曰，「中堅剛」、終莫傾也。',
  '次四': '卵破石，毈。測曰，「卵破」之「毈」、小人難也。',
  '次五': '難無間，雖大不勤。測曰，「難無間」、中密塞也。',
  '次六': '大車川川，上輆于山，下觸于川。測曰，「大車川川」、上下輆也。',
  '次七': '拔石䂦䂦，力沒以盡。測曰，「拔石䂦䂦」、乘時也。',
  '次八': '觸石決木，維折角。測曰，「觸石決木」、非所治也。',
  '上九': '角解豸，終以直，其有施。測曰，「角解豸」、終以直之也。'},

3332:{'卦': {'勤': '陰凍沍，戁創於外，微陽邸冥，膂力於內。'},
  '初一': '勤于心，否貞。測曰，「勤」「否貞」、中不正也。',
  '次二': '勞有恩，勤悾悾，君子有中。測曰，「勞有恩」，「勤」有諸情也。',
  '次三': '羈角之吾，其泣呱呱，未得繈杖。測曰，「羈角之吾」、不得命也。',
  '次四': '勤于力，放倍忘食，大人有克。測曰，「勤力」「忘食」、大人德也。',
  '次五': '往蹇蹇，禍邇福遠。測曰，「往」之「蹇蹇」、遠乎福也。',
  '次六': '勤有成功，幾于天。測曰，「勤有成功」、天所來輔也。',
  '次七': '勞牽不其鼻，于尾，弊。測曰，「勞牽」之「弊」、其道逆也。',
  '次八': '勞踖踖，心爽，蒙柴不卻。測曰，「勞踖踖」、躬殉國也。',
  '上九': '其勤其勤，抱車入淵，負舟上山。測曰，「其勤其勤」、勞不得也。'},
 
 3333:{'卦': {'養': '陰弸于野，陽蓲萬物，赤之於下。'},
  '初一': '藏心于淵，美厥靈根。測曰，「藏心于淵」、神不外也。',
  '次二': '墨養邪，元函否貞。測曰，「墨養邪」、中心敗也。',
  '次三': '糞以肥丘，育厥根荄。測曰，「糞以肥丘」、中光大也。',
  '次四': '燕食扁扁，其志𠑩𠑩，利用征賈。測曰，「燕食扁扁」、志在賴也。',
  '次五': '黃心在腹，白骨生肉，孚德不復。測曰，「黃心在腹」、上得天也。',
  '次六': '次次，一日三餼，祇牛之兆，肥不利。測曰，「次次」之「餼」、肥無身也。',
  '次七': '小子牽象，婦人徽猛，君子養病。測曰，「牽象」「養病」、不相因也。',
  '次八': '鯁不脫，毒疾發，鬼上壟。測曰，「鯁」「疾」之「發」、歸于墳也。',
  '上九': '星如歲如，復繼之初。測曰，「星如歲如」、終始養也。'}
}
